CREATE TABLE `user` ( `user_id` varchar(36) NOT NULL, `name` varchar(100) NOT NULL, `login` varchar(100) NOT NULL, `pwd` varchar(255) NOT NULL, `confirmed` tinyint(1) DEFAULT NULL, `token` varchar(255) DEFAULT NULL, RIMARY KEY (`user_id`), UNIQUE KEY `unique_login` (`login`), UNIQUE KEY `unique_token` (`token`));
CREATE TABLE `mail` ( `mail_id` int NOT NULL AUTO_INCREMENT, `action` varchar(10) NOT NULL, `creation` timestamp NOT NULL, `token` varchar(36) NOT NULL, `user_id` varchar(255) DEFAULT NULL, PRIMARY KEY (`mail_id`), KEY `fk_user_id` (`user_id`), CONSTRAINT `fk_user_id` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE);
